			, BOOK_INFO = #{BOOK_INFO}
			, BOOK_MEMO = #{BOOK_MEMO}
			, BOOK_KEYWORD = #{BOOK_KEYWORD}
			, ISSUE_DATE = #{ISSUE_DATE}
