			, BOOK_AUTHOR = #{BOOK_AUTHOR}
			, BOOK_SUPPLEMENTDATA = #{BOOK_SUPPLEMENTDATA}
			, BOOK_PRINTINGDATE = #{BOOK_PRINTINGDATE}
