mapper.select:
select	count(*)
from	tb_delivers
where	orderNo = #{orderno}

select a.leccode mgntNo, b.sdate, c.orderNo, c.USER_ID userId, '동영상강좌' gbnName, a.subject_title name, a.subject_price price, a.SUBJECT_DISCOUNT rate, (case b.wmv_pmp when 'VOD' then a.SUBJECT_MOVIE when 'PMP' then a.SUBJECT_PMP when 'VOD+PMP' then a.SUBJECT_MOVIE_PMP end) as realprice, b.cnt, b.statusCode , b.wmv_pmp, (b.Price * b.cnt) tot_price, b.statusCode statusCodeName, NVL(b.discountper,0) discountper, b.iscancel from TB_LEC_MST a, tb_order_mgnt_no b, tb_orders c where a.leccode = b.mgntNo and b.orderNo = c.orderNo and c.orderNo = #{orderno}
union all
select a.rsc_id mgntNo, b.sdate, c.orderNo, c.USER_ID userId, '도서' gbnName, a.book_nm name, a.price price, a.discount rate, b.price realprice, b.cnt, b.statusCode , '도서' wmv_pmp,
(b.Price) tot_price, b.statusCode statusCodeName, NVL(b.discountper,0) discountper, b.iscancel from TB_CA_BOOK a, tb_order_mgnt_no b, tb_orders c where a.rsc_id = b.mgntNo and b.orderNo = c.orderNo and c.orderNo = #{orderno}

mapper.update:
update	tb_approvals
	set
		payName = #{payname}
where	orderNo = #{orderno}

and USERNAME = #{rec_name}

  update	tb_order_mgnt_no
	  set
	  	wmv_pmp = #{wmv_pmp}
  where	orderNo = #{orderno}
  and mgntNO = #{mgntno}

update tb_order_mgnt_no
	set
		price = #{realprice}
where orderNo = #{orderno}
and mgntNo = #{mgntno}
and isCancel = #{iscancle}

mapper.select:
select sum(price)
from tb_order_mgnt_no
where orderNo = #{orderno}

mapper.update:
update tb_approvals
	set price = #{totalPrice}
where orderNo = #{orderno}

mapper.delete:
delete tb_order_mgnt_no
where mgntno = #{mgntno}
and orderno = #{orderno}
and iscancel = #{iscancle}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, H.* FROM ( SELECT A.ORDERNO, A.ORDERNO AS ORDERS_ORDERNO, A.USER_NM AS ORDERS_USERNAME, A.USER_ID AS ORDERS_USERID, A.TEL_NO AS ORDERS_TELNO, A.PHONE_NO AS ORDERS_CELLNO, A.ZIP_CODE AS ORDERS_ZIPCD, A.ADDRESS1 || ADDRESS2 AS ORDERS_ADDR, A.EMAIL AS ORDERS_EMAIL, TO_CHAR(A.REG_DT, 'YYYYMMDD') AS ORDERS_REGDATE, A.IPDATE AS ORDERS_IPDATE, B.SENDNO AS DELIVERS_SENDNO, B.USERNAME AS DELIVERS_USERNAME, '' AS DELIVERS_JUMIN1, '' AS DELIVERS_JUMIN2, CASE WHEN NVL(B.TELNO, '') = '' THEN B.CELLNO ELSE B.TELNO END DELIVERS_TELNO, B.ZIPCD AS DELIVERS_ZIPCD, B.ADDR AS DELIVERS_ADDR, B.MEMO AS DELIVERS_MEMO, TO_CHAR(B.REGDATE, 'YYYY-MM-DD') AS DELIVERS_REGDATE, TO_CHAR(B.SENDDATE, 'YYYY-MM-DD') AS DELIVERS_SENDDATE, B.DLEORDER AS DLEORDER, C.PRICE AS APPROVALS_PRICE, C.ADDPRICE AS APPROVALS_ADDPRICE, C.PAYCODE AS APPROVALS_PAYCODE, (SELECT CODE_NM FROM TB_BA_CONFIG_CD WHERE SYS_CD = 'PAYMENT_CODE' AND CODE_VAL =C.PAYCODE ) AS APPROVALS_PAYCODE_NM , C.ACCTNOCODE AS APPROVALS_ACCTNOCODE, C.PAYNAME AS APPROVALS_PAYNAME, C.POINT AS APPROVALS_POINT, C.RETURNVALUE AS APPROVALS_RETURNVALUE, TO_CHAR(C.REG_DT, 'YYYYMMDD') AS APPROVALS_REGDATE,
C.PAYCODE APPROVALS_PAYCODENAME, C.ACCTNOCODE APPROVALS_ACCTNONAME, B.SENDNO TBL_DELIVERS_SENDNO, (SELECT MAX(STATUSCODE) FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND ISCANCEL = 0) STATUSCODE
FROM TB_ORDERS A LEFT JOIN TB_DELIVERS B ON A.ORDERNO = B.ORDERNO, TB_APPROVALS C WHERE C.ORDERNO = A.ORDERNO AND A.ORDERNO IN (SELECT DISTINCT AA.ORDERNO FROM TB_ORDER_MGNT_NO AA, TB_CA_BOOK BB WHERE AA.ORDERNO = A.ORDERNO AND AA.MGNTNO = BB.RSC_ID AND AA.STATUSCODE IN ('DLV105', 'DLV110', 'DLV120', 'DLV130')) AND (B.DLEORDER = 'DLE110') and TO_CHAR(a.REG_DT, 'YYYYMMDD') between #{sdate} and #{edate}
and ${searchtype} like '%'||#{searchkey}||'%'
and ( a.orderNo like '%'||#{searchkey}||'%' or a.USER_ID like '%'||#{searchkey}||'%' or a.USER_NM like '%'||#{searchkey}||'%' or b.username like '%'||#{searchkey}||'%' or c.payName like '%'||#{searchkey}||'%' )
ORDER BY A.IPDATE ASC , A.ORDERNO ASC ) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}

SELECT * FROM ( SELECT ROWNUM AS RNUM, H.* FROM (
select b.orderNo , c.USER_ID userid , c.USER_NM username , b.userName deliveName , case when nvl(b.cellNo, '') = '' then b.telNo else b.cellNo end cellNo , b.zipcd , b.addr , TO_CHAR(b.sendDate, 'YYYY-MM-DD') sendDate , b.sendNo , d.addPrice from ( select distinct ta.orderNo from tb_order_mgnt_no ta, TB_CA_BOOK tb where ta.mgntNo = tb.rsc_id and ta.statusCode IN ('DLV120', 'DLV125', 'DLV130')
and ${searchtype} like '%'||#{searchkey}||'%'
and ${searchtype} like '%'||#{searchkey}||'%'
) a, tb_delivers b, tb_orders c, tb_approvals d where a.orderNo = b.orderNo and b.orderNo = c.orderNo and c.orderNo = d.orderNo
and TO_CHAR(b.sendDate, 'YYYYMMDD') between #{sdate} and #{edate}
and ${searchtype} like '%'||#{searchkey}||'%'
and ( b.orderNo like '%'||#{searchkey}||'%' or c.USER_ID like '%'||#{searchkey}||'%' or c.USER_NM like '%'||#{searchkey}||'%' or b.username like '%'||#{searchkey}||'%' or d.payName like '%'||#{searchkey}||'%' )
order by b.sendDate desc
) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}
