mapper.insert:
INSERT INTO TB_CATEGORY_INFO(
    ID, CODE, NAME, ISUSE, USE_ON, USE_OFF
    , REG_DT, REG_ID, UPD_DT, UPD_ID
 , P_CODE
    , ORDR
) VALUES (
    SEQ_TB_CATEGORY_INFO_ID.NEXTVAL, #{CODE}, #{NAME}, #{ISUSE}, #{USE_ON}, #{USE_OFF}
    , SYSDATE, #{REG_ID}, SYSDATE, #{UPD_ID}
 , #{P_CODE}

     , #{ORDR} 


        , ( SELECT (NVL(MAX(ORDR), 0) + 1)  ORDR FROM TB_CATEGORY_INFO WHERE P_CODE=#{P_CODE})
        , 1



)

mapper.update:
UPDATE TB_CATEGORY_INFO
SET
  NAME = #{NAME}
  , ISUSE = #{ISUSE}
  , USE_ON = #{USE_ON}
  , USE_OFF = #{USE_OFF}
  , ORDR = #{ORDR} 
  , UPD_DT = SYSDATE
  , UPD_ID = #{UPD_ID}
WHERE CODE = #{CODE}

mapper.delete:
UPDATE TB_CATEGORY_INFO
SET ISUSE = 'N'
WHERE CODE = #{CODE}

select.if:
SELECT
    USER_ID, CATEGORY_CODE, ORDR FROM
TB_MA_MEMBER_CATEGORY WHERE USER_ID = #{USER_ID}
