mapper.select:
SELECT TBL.* FROM (SELECT A.*, ROWNUM RNUM FROM (SELECT TBL.*, NVL(YY.VODY, 0) VODY, NVL(YY.VODN, 0) VODN, NVL(YY.PMPY, 0) PMPY, NVL(YY.PMPN, 0) PMPN, NVL(YY.VODPMPY, 0) VODPMPY, NVL(YY.VODPMPN, 0) VODPMPN FROM (SELECT LM.SEQ, LM.LEC_TYPE_CHOICE, LM.LECCODE, AA.BRIDGE_LECCODE, LM.SUBJECT_OPTION, LM.CATEGORY_CD, (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = LM.CATEGORY_CD) AS CATEGORY_NM, LM.SUBJECT_SJT_CD, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = LM.SUBJECT_SJT_CD) AS SUBJECT_NM, LM.SUBJECT_TEACHER, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = LM.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM, LM.SUBJECT_TITLE, NVL(LM.LEC_BAESU, '00') LEC_BAESU, LM.LEARNING_CD, (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = LM.LEARNING_CD) AS LEARNING_NM, LM.REG_DT, LM.SUBJECT_ISUSE,DECODE(LM.FREE_TAB, 'TAB_001', '입문특강', 'TAB_002', '테마별특강', 'TAB_003', '마무리특강', 'TAB_004', '모의고사해설', 'TAB_005', '기출해설특강', 'TAB_006', '합격설명회', 'TAB_007', '보강동영상', '입문특강') FREE_TAB FROM TB_LEC_MST LM, (SELECT A.LECCODE, B.BRIDGE_LECCODE, RANK () OVER(PARTITION BY B.BRIDGE_LECCODE ORDER BY A.LECCODE ASC, A.SEQ ASC) RANK FROM TB_LEC_MST A, TB_LEC_BRIDGE B WHERE A.LECCODE = B.LECCODE
AND A.CATEGORY_CD = #{SEARCHKIND}
) AA WHERE LM.LECCODE = AA.LECCODE AND AA.RANK = 1) TBL, (SELECT ZZ.LECCODE, NVL(SUM(VOD_N_CNT),0) VODY, NVL(SUM(VOD_Y_CNT),0) VODN, NVL(SUM(PMP_N_CNT),0) PMPY, NVL(SUM(PMP_Y_CNT),0) PMPN, NVL(SUM(MP4_N_CNT),0) VODPMPY, NVL(SUM(MP4_Y_CNT),0) VODPMPN FROM (SELECT CC.LECCODE, MM.BRIDGE_LECCODE, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD' THEN CNT END VOD_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD' THEN CNT END VOD_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'PMP' THEN CNT END PMP_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'PMP' THEN CNT END PMP_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_N_CNT, MM.WMV_PMP, CNT FROM (SELECT BB.BRIDGE_LECCODE, DECODE (OM.PRICE, 0, 'Y', 'N') FREE, WMV_PMP, COUNT (OM.MGNTNO) CNT FROM TB_LEC_MST LM, TB_ORDER_MGNT_NO OM, (SELECT B.ORDERNO, B.MGNTNO, C.BRIDGE_LECCODE FROM TB_MYLECTURE A, TB_ORDER_MGNT_NO B, TB_LEC_BRIDGE C WHERE A.ORDERNO = B.ORDERNO AND A.PACKAGE_NO = B.MGNTNO AND A.LECTURE_NO = C.LECCODE AND B.STATUSCODE = 'DLV105' AND B.PTYPE = #{LEC_TYPE_CHOICE} GROUP BY B.ORDERNO, B.MGNTNO, C.BRIDGE_LECCODE) BB WHERE OM.ORDERNO = BB.ORDERNO AND OM.MGNTNO = BB.MGNTNO AND LM.LECCODE = OM.MGNTNO GROUP BY BB.BRIDGE_LECCODE, DECODE (OM.PRICE, 0, 'Y', 'N'), WMV_PMP) MM, TB_LEC_BRIDGE CC WHERE MM.BRIDGE_LECCODE = CC.BRIDGE_LECCODE) ZZ GROUP BY LECCODE) YY WHERE 1 = 1 AND TBL.LECCODE = YY.LECCODE(+) AND LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.SUBJECT_ISUSE = #{SEARCHISUSE}
AND TBL.SUBJECT_OFF_OPEN_YEAR >= '2012'
AND TBL.LEC_BAESU = #{S_LEC_BAESU}
AND (INSTR(TBL.SUBJECT_SJT_CD, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_NM, #{SEARCHTEXT}) > 0)
AND INSTR(TBL.SUBJECT_TITLE, #{SEARCHTEXT}) > 0
AND INSTR(TBL.SUBJECT_TEACHER_NM, #{SEARCHTEXT}) > 0
AND (INSTR(TBL.BRIDGE_LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(TBL.BRIDGE_LECCODE, (SELECT BRIDGE_LECCODE FROM TB_LEC_BRIDGE WHERE LECCODE = #{SEARCHTEXT})) > 0)
AND (INSTR(TBL.SUBJECT_SJT_CD, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_NM, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_TEACHER_NM, #{SEARCHTEXT}) > 0 OR INSTR(TBL.BRIDGE_LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(TBL.BRIDGE_LECCODE, (SELECT BRIDGE_LECCODE FROM TB_LEC_BRIDGE WHERE LECCODE = #{SEARCHTEXT})) > 0)
ORDER BY TBL.SEQ DESC ) A WHERE ROWNUM <= #{endNo} ) TBL WHERE RNUM > #{startNo}

SELECT COUNT(TBL.LECCODE) FROM ( SELECT LM.SEQ, LM.LEC_TYPE_CHOICE, LM.LECCODE, AA.BRIDGE_LECCODE, LM.SUBJECT_OPTION, LM.CATEGORY_CD, (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = LM.CATEGORY_CD) AS CATEGORY_NM, LM.SUBJECT_SJT_CD, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = LM.SUBJECT_SJT_CD) AS SUBJECT_NM, LM.SUBJECT_TEACHER, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = LM.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM, LM.SUBJECT_TITLE, NVL(LM.LEC_BAESU, '00') LEC_BAESU, LM.LEARNING_CD, (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = LM.LEARNING_CD) AS LEARNING_NM, LM.REG_DT, LM.SUBJECT_ISUSE FROM TB_LEC_MST LM, (SELECT A.LECCODE, B.BRIDGE_LECCODE, RANK () OVER(PARTITION BY B.BRIDGE_LECCODE ORDER BY A.LECCODE ASC, A.SEQ ASC) RANK FROM TB_LEC_MST A, TB_LEC_BRIDGE B WHERE A.LECCODE = B.LECCODE
AND A.CATEGORY_CD = #{SEARCHKIND}
) AA WHERE LM.LECCODE = AA.LECCODE AND AA.RANK = 1) TBL, (SELECT ZZ.LECCODE, NVL(SUM(VOD_N_CNT),0) VODY, NVL(SUM(VOD_Y_CNT),0) VODN, NVL(SUM(PMP_N_CNT),0) PMPY, NVL(SUM(PMP_Y_CNT),0) PMPN, NVL(SUM(MP4_N_CNT),0) VODPMPY, NVL(SUM(MP4_Y_CNT),0) VODPMPN FROM (SELECT CC.LECCODE, MM.BRIDGE_LECCODE, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD' THEN CNT END VOD_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD' THEN CNT END VOD_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'PMP' THEN CNT END PMP_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'PMP' THEN CNT END PMP_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_N_CNT, MM.WMV_PMP, CNT FROM (SELECT BB.BRIDGE_LECCODE, DECODE (OM.PRICE, 0, 'Y', 'N') FREE, WMV_PMP, COUNT (OM.MGNTNO) CNT FROM TB_LEC_MST LM, TB_ORDER_MGNT_NO OM, (SELECT B.ORDERNO, B.MGNTNO, C.BRIDGE_LECCODE FROM TB_MYLECTURE A, TB_ORDER_MGNT_NO B, TB_LEC_BRIDGE C WHERE A.ORDERNO = B.ORDERNO AND A.PACKAGE_NO = B.MGNTNO AND A.LECTURE_NO = C.LECCODE AND B.STATUSCODE = 'DLV105' AND B.PTYPE = #{LEC_TYPE_CHOICE} GROUP BY B.ORDERNO, B.MGNTNO, C.BRIDGE_LECCODE) BB WHERE OM.ORDERNO = BB.ORDERNO AND OM.MGNTNO = BB.MGNTNO AND LM.LECCODE = OM.MGNTNO GROUP BY BB.BRIDGE_LECCODE, DECODE (OM.PRICE, 0, 'Y', 'N'), WMV_PMP) MM, TB_LEC_BRIDGE CC WHERE MM.BRIDGE_LECCODE = CC.BRIDGE_LECCODE) ZZ GROUP BY LECCODE) YY WHERE 1 = 1 AND TBL.LECCODE = YY.LECCODE(+) AND LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.SUBJECT_ISUSE = #{SEARCHISUSE}
AND TBL.SUBJECT_OFF_OPEN_YEAR >= '2012'
AND TBL.LEC_BAESU = #{S_LEC_BAESU}
AND (INSTR(TBL.SUBJECT_SJT_CD, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_NM, #{SEARCHTEXT}) > 0)
AND INSTR(TBL.SUBJECT_TITLE, #{SEARCHTEXT}) > 0
AND INSTR(TBL.SUBJECT_TEACHER_NM, #{SEARCHTEXT}) > 0
AND (INSTR(TBL.BRIDGE_LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(TBL.BRIDGE_LECCODE, (SELECT BRIDGE_LECCODE FROM TB_LEC_BRIDGE WHERE LECCODE = #{SEARCHTEXT})) > 0)
AND (INSTR(TBL.SUBJECT_SJT_CD, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_NM, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 OR INSTR(TBL.SUBJECT_TEACHER_NM, #{SEARCHTEXT}) > 0 OR INSTR(TBL.BRIDGE_LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(TBL.BRIDGE_LECCODE, (SELECT BRIDGE_LECCODE FROM TB_LEC_BRIDGE WHERE LECCODE = #{SEARCHTEXT})) > 0)

		SELECT LTBL.*
			,(SELECT NAME FROM TB_CATEGORY_INFO WHERE ISUSE = 'Y' AND CODE = LTBL.CATEGORY_CD) CATEGORY_NM
			,(SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE ISUSE = 'Y' AND CODE = LTBL.LEARNING_CD) LEARNING_NM
		FROM 	(
					SELECT TBL.*, ROWNUM rnum
				  	FROM (
						SELECT
						  SEQ, RSC_ID, SUBJECT_SJT_CD, CATEGORY_CD, LEARNING_CD, BOOK_NM
						FROM TB_CA_BOOK WHERE USE_YN = 'Y'

					 		AND CATEGORY_CD = #{SEARCHKIND}


					 		AND LEARNING_CD = #{SEARCHFORM}


							AND BOOK_NM like '%'||#{SEARCHTEXT}||'%'

						ORDER BY REG_DT DESC
				) TBL
               	WHERE rownum <= #{endNo}
			) LTBL
		WHERE rnum > #{startNo}

	SELECT
	  COUNT(RSC_ID)
	FROM TB_CA_BOOK WHERE USE_YN = 'Y'

 		AND CATEGORY_CD = #{SEARCHKIND}


 		AND LEARNING_CD = #{SEARCHFORM}


		AND BOOK_NM like '%'||#{SEARCHTEXT}||'%'

