mapper.select:
SELECT TBL.* FROM ( SELECT T1.BRIDGE_LECCODE, T2.* , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = T2.CATEGORY_CD) AS CATEGORY_NM , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = T2.LEARNING_CD) AS LEARNING_NM , (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = T2.SUBJECT_SJT_CD) AS SUBJECT_NM , (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = T2.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM FROM TB_LEC_BRIDGE T1 RIGHT JOIN TB_LEC_MST T2 ON T1.LECCODE = T2.LECCODE WHERE T2.SUBJECT_PRICE <> 0 ) TBL WHERE 1 = 1 AND LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
SELECT TBL.* , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = TBL.CATEGORY_CD) AS CATEGORY_NM , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = TBL.LEARNING_CD) AS LEARNING_NM FROM TB_LEC_MST TBL WHERE LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE} AND TBL.SUBJECT_PRICE <> 0
AND 1 = 2
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.CATEGORY_CD = #{SEARCHKIND}
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%')
AND TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
AND (TBL.BRIDGE_LECCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.LECCODE like '%'||#{SEARCHTEXT}||'%')
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.BRIDGE_LECCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.LECCODE like '%'||#{SEARCHTEXT}||'%')
ORDER BY TBL.SEQ DESC

SELECT LTBL.* , (SELECT COUNT(A.LECCODE) FROM TB_LEC_MST A ,( SELECT A.ORDERNO, A.MGNTNO, A.WMV_PMP FROM TB_ORDER_MGNT_NO A ,(SELECT ORDERNO ,MGNTNO FROM TB_ORDER_MGNT_NO GROUP BY ORDERNO, MGNTNO HAVING COUNT(*) = 1) B WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.MGNTNO AND A.STATUSCODE = 'DLV105' AND SUBSTR(A.MGNTNO,1,1) <> 'L' AND A.WMV_PMP = 'VOD' AND A.PRICE > 0 ) B ,TB_MYLECTURE C WHERE A.LECCODE = C.LECTURE_NO AND B.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND SUBSTR(C.PACKAGE_NO,1,1) NOT IN ('D','L') AND B.WMV_PMP = 'VOD' AND C.PACKAGE_NO = LTBL.LECCODE) VODY , (SELECT COUNT(A.LECCODE) FROM TB_LEC_MST A ,( SELECT A.ORDERNO, A.MGNTNO, A.WMV_PMP FROM TB_ORDER_MGNT_NO A ,(SELECT ORDERNO ,MGNTNO FROM TB_ORDER_MGNT_NO GROUP BY ORDERNO, MGNTNO HAVING COUNT(*) = 1) B WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.MGNTNO AND A.STATUSCODE = 'DLV105' AND SUBSTR(A.MGNTNO,1,1) <> 'L' AND A.WMV_PMP = 'VOD' AND A.PRICE = 0 ) B ,TB_MYLECTURE C WHERE A.LECCODE = C.LECTURE_NO AND B.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND SUBSTR(C.PACKAGE_NO,1,1) NOT IN ('D','L') AND B.WMV_PMP = 'VOD' AND C.PACKAGE_NO = LTBL.LECCODE) VODN , (SELECT COUNT(A.LECCODE) FROM TB_LEC_MST A ,( SELECT A.ORDERNO, A.MGNTNO, A.WMV_PMP FROM TB_ORDER_MGNT_NO A ,(SELECT ORDERNO ,MGNTNO FROM TB_ORDER_MGNT_NO GROUP BY ORDERNO, MGNTNO HAVING COUNT(*) = 1) B WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.MGNTNO AND A.STATUSCODE = 'DLV105' AND SUBSTR(A.MGNTNO,1,1) <> 'L' AND A.WMV_PMP = 'PMP' AND A.PRICE > 0 ) B ,TB_MYLECTURE C WHERE A.LECCODE = C.LECTURE_NO AND B.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND SUBSTR(C.PACKAGE_NO,1,1) NOT IN ('D','L') AND B.WMV_PMP = 'PMP' AND C.PACKAGE_NO = LTBL.LECCODE) PMPY , (SELECT COUNT(A.LECCODE) FROM TB_LEC_MST A ,( SELECT A.ORDERNO, A.MGNTNO, A.WMV_PMP FROM TB_ORDER_MGNT_NO A ,(SELECT ORDERNO ,MGNTNO FROM TB_ORDER_MGNT_NO GROUP BY ORDERNO, MGNTNO HAVING COUNT(*) = 1) B WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.MGNTNO AND A.STATUSCODE = 'DLV105' AND