							TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
							OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
						)



                    AND TBL.SUBJECT_SJT_CD = #{SEARCHSBJTCD}


                    AND TBL.SUBJECT_TEACHER = #{SEARCHPRFID}

				ORDER BY TBL.SEQ DESC
			) A
            WHERE rownum <= #{endNo}
		)
