mapper.select:
SELECT TBL.* FROM (SELECT A.*, ROWNUM rnum FROM (SELECT TBL.*, NVL(YY.VODY, 0) VODY, NVL(YY.VODN, 0) VODN, NVL(YY.PMPY, 0) PMPY, NVL(YY.PMPN, 0) PMPN, NVL(YY.VODPMPY, 0) VODPMPY, NVL(YY.VODPMPN, 0) VODPMPN FROM (SELECT LM.SEQ, LM.MSTCODE, AA.BRIDGE_MSTCODE, LM.SUBJECT_OPTION, LM.SUBJECT_SJT_CD, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = LM.SUBJECT_SJT_CD) AS SUBJECT_NM, LM.SUBJECT_TEACHER, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = LM.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM, LM.SUBJECT_TITLE, LM.REG_DT FROM TB_TOP_MST LM, (SELECT A.MSTCODE, B.BRIDGE_MSTCODE, RANK () OVER(PARTITION BY B.BRIDGE_MSTCODE ORDER BY A.MSTCODE ASC, A.SEQ ASC) RANK FROM TB_TOP_MST A, TB_MST_BRIDGE B WHERE A.MSTCODE = B.MSTCODE
AND A.CATEGORY_CD = #{SEARCHKIND}
) AA WHERE LM.MSTCODE = AA.MSTCODE AND AA.RANK = 1) TBL, (SELECT ZZ.MSTCODE, NVL(SUM(VOD_N_CNT),0) VODY, NVL(SUM(VOD_Y_CNT),0) VODN, NVL(SUM(PMP_N_CNT),0) PMPY, NVL(SUM(PMP_Y_CNT),0) PMPN, NVL(SUM(MP4_N_CNT),0) VODPMPY, NVL(SUM(MP4_Y_CNT),0) VODPMPN FROM (SELECT CC.MSTCODE, MM.BRIDGE_MSTCODE, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD' THEN CNT END VOD_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD' THEN CNT END VOD_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'PMP' THEN CNT END PMP_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'PMP' THEN CNT END PMP_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_N_CNT, MM.WMV_PMP, CNT FROM (SELECT BB.BRIDGE_MSTCODE, DECODE (OM.PRICE, 0, 'Y', 'N') FREE, WMV_PMP, COUNT (OM.MGNTNO) CNT FROM TB_TOP_MST LM, TB_ORDER_MGNT_NO OM, (SELECT B.ORDERNO, B.MGNTNO, C.BRIDGE_MSTCODE FROM TB_MYLECTURE A, TB_ORDER_MGNT_NO B, TB_MST_BRIDGE C, (SELECT A.MSTCODE, B.BRIDGE_MSTCODE FROM TB_TOP_MST A, TB_MST_BRIDGE B WHERE A.MSTCODE = B.MSTCODE) D WHERE A.ORDERNO = B.ORDERNO AND A.PACKAGE_NO = B.MGNTNO AND A.LECTURE_NO = C.MSTCODE AND B.STATUSCODE IN ('DLV105', 'DLV230') AND C.MSTCODE = D.MSTCODE AND C.BRIDGE_MSTCODE = D.BRIDGE_MSTCODE GROUP BY B.ORDERNO, B.MGNTNO, C.BRIDGE_MSTCODE HAVING COUNT(B.MGNTNO) = 1) BB WHERE OM.ORDERNO = BB.ORDERNO AND OM.MGNTNO = BB.MGNTNO AND LM.MSTCODE = OM.MGNTNO GROUP BY BB.BRIDGE_MSTCODE, DECODE (OM.PRICE, 0, 'Y', 'N'), WMV_PMP) MM, TB_MST_BRIDGE CC WHERE MM.BRIDGE_MSTCODE = CC.BRIDGE_MSTCODE) ZZ GROUP BY MSTCODE) YY WHERE 1 = 1 AND TBL.MSTCODE = YY.MSTCODE(+)
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.SUBJECT_OFF_OPEN_YEAR >= '2012'
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%')
AND TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
AND (TBL.BRIDGE_MSTCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.MSTCODE like '%'||#{SEARCHTEXT}||'%')
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.BRIDGE_MSTCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.MSTCODE like '%'||#{SEARCHTEXT}||'%')
ORDER BY TBL.SEQ DESC ) A WHERE rownum <= #{endNo} ) TBL WHERE rnum > #{startNo}

SELECT COUNT(TBL.MSTCODE) FROM ( SELECT LM.SEQ, LM.MSTCODE, AA.BRIDGE_MSTCODE, LM.SUBJECT_OPTION, LM.SUBJECT_TEACHER, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = LM.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM, LM.SUBJECT_TITLE, LM.REG_DT FROM TB_TOP_MST LM, (SELECT A.MSTCODE, B.BRIDGE_MSTCODE, RANK () OVER(PARTITION BY B.BRIDGE_MSTCODE ORDER BY A.MSTCODE ASC, A.SEQ ASC) RANK FROM TB_TOP_MST A, TB_MST_BRIDGE B WHERE A.MSTCODE = B.MSTCODE
AND A.CATEGORY_CD = #{SEARCHKIND}
) AA WHERE LM.MSTCODE = AA.MSTCODE AND AA.RANK = 1) TBL, (SELECT ZZ.MSTCODE, NVL(SUM(VOD_N_CNT),0) VODY, NVL(SUM(VOD_Y_CNT),0) VODN, NVL(SUM(PMP_N_CNT),0) PMPY, NVL(SUM(PMP_Y_CNT),0) PMPN, NVL(SUM(MP4_N_CNT),0) VODPMPY, NVL(SUM(MP4_Y_CNT),0) VODPMPN FROM (SELECT CC.MSTCODE, MM.BRIDGE_MSTCODE, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD' THEN CNT END VOD_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD' THEN CNT END VOD_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'PMP' THEN CNT END PMP_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'PMP' THEN CNT END PMP_N_CNT, CASE WHEN MM.FREE = 'Y' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_Y_CNT, CASE WHEN MM.FREE = 'N' AND WMV_PMP = 'VOD+PMP' THEN CNT END MP4_N_CNT, MM.WMV_PMP, CNT FROM (SELECT BB.BRIDGE_MSTCODE, DECODE (OM.PRICE, 0, 'Y', 'N') FREE, WMV_PMP, COUNT (OM.MGNTNO) CNT FROM TB_TOP_MST LM, TB_ORDER_MGNT_NO OM, (SELECT B.ORDERNO, B.MGNTNO, C.BRIDGE_MSTCODE FROM TB_MYLECTURE A, TB_ORDER_MGNT_NO B, TB_MST_BRIDGE C, (SELECT A.MSTCODE, B.BRIDGE_MSTCODE FROM TB_TOP_MST A, TB_MST_BRIDGE B WHERE A.MSTCODE = B.MSTCODE) D WHERE A.ORDERNO = B.ORDERNO AND A.PACKAGE_NO = B.MGNTNO AND A.LECTURE_NO = C.MSTCODE AND B.STATUSCODE IN ('DLV105', 'DLV230') AND C.MSTCODE = D.MSTCODE AND C.BRIDGE_MSTCODE = D.BRIDGE_MSTCODE GROUP BY B.ORDERNO, B.MGNTNO, C.BRIDGE_MSTCODE HAVING COUNT(B.MGNTNO) = 1) BB WHERE OM.ORDERNO = BB.ORDERNO AND OM.MGNTNO = BB.MGNTNO AND LM.MSTCODE = OM.MGNTNO GROUP BY BB.BRIDGE_MSTCODE, DECODE (OM.PRICE, 0, 'Y', 'N'), WMV_PMP) MM, TB_MST_BRIDGE CC WHERE MM.BRIDGE_MSTCODE = CC.BRIDGE_MSTCODE) ZZ GROUP BY MSTCODE) YY WHERE 1 = 1 AND TBL.MSTCODE = YY.MSTCODE(+)
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.SUBJECT_OFF_OPEN_YEAR >= '2012'
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%')
AND TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
AND (TBL.BRIDGE_MSTCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.MSTCODE like '%'||#{SEARCHTEXT}||'%')
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.BRIDGE_MSTCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.MSTCODE like '%'||#{SEARCHTEXT}||'%')

SELECT DISTINCT A.CONTENTID FROM TB_PMP_DOWNLOG A, TB_MOVIE B
WHERE A.CONTENTID = B.MOVIE_NO
AND A.USERID = #{ORDERID}
AND B.MSTCODE = #{BRIDGE_MSTCODE}
AND A.DOWNLOGINFO LIKE '%'||#{ORDERNO}||'-'||#{MSTCODE}||'%'

mapper.delete:
DELETE FROM TB_PLUS_CA_BOOK
WHERE MSTCODE IN (SELECT MSTCODE FROM TB_MST_BRIDGE WHERE BRIDGE_MSTCODE = #{BRIDGE_LEC} )
WHERE MSTCODE = #{MSTCODE}
