mapper.select:
SELECT * FROM (SELECT DA.*, (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = DA.CATEGORY_CD) AS CATEGORY_NM, (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = DA.LEARNING_CD) AS LEARNING_NM, NVL((SELECT MAX(Z.LEC_DATE) FROM TB_OFF_LECTURE_DATE Z WHERE Z.LECCODE = DA.LECCODE),'') AS SUBJECT_END_DATE, ROWNUM rnum FROM (SELECT CA.CNT OFFERERCNT, CA.SUBJECT_TEACHER_NM, CA.SUBJECT_NM, CA.BRIDGE_LECCODE, CB.* FROM (SELECT BA.LECCODE, BA.SUBJECT_ISUSE, BA.LEC_PROCESS, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = BA.SUBJECT_SJT_CD) AS SUBJECT_NM, BA.CATEGORY_CD, BA.LEARNING_CD, BA.SUBJECT_TITLE, BA.SUBJECT_TEACHER, BC.USER_NM SUBJECT_TEACHER_NM, BB.BRIDGE_LECCODE, RANK () OVER(PARTITION BY BB.BRIDGE_LECCODE ORDER BY BA.CATEGORY_CD ASC) RANK, NVL(BD.CNT, 0) CNT FROM TB_OFF_LEC_MST BA, TB_OFF_LEC_BRIDGE BB, TB_MA_MEMBER BC, (SELECT BRIDGE_LECCODE, COUNT(BRIDGE_LECCODE) CNT FROM (SELECT AC.BRIDGE_LECCODE FROM TB_OFF_MYLECTURE AA, TB_OFF_ORDER_MGNT_NO AB, TB_OFF_LEC_BRIDGE AC, (SELECT A.LECCODE, B.BRIDGE_LECCODE FROM TB_OFF_LEC_MST A, TB_OFF_LEC_BRIDGE B WHERE A.LECCODE = B.LECCODE) AD WHERE AA.ORDERNO = AB.ORDERNO AND AA.PACKAGE_NO = AB.MGNTNO AND AA.LECTURE_NO = AC.LECCODE AND AB.STATUSCODE IN ('DLV105', 'DLV230') AND AC.LECCODE = AD.LECCODE AND AC.BRIDGE_LECCODE = AD.BRIDGE_LECCODE GROUP BY AB.ORDERNO, AB.MGNTNO, AC.BRIDGE_LECCODE HAVING COUNT(AB.MGNTNO) = 1) GROUP BY BRIDGE_LECCODE) BD WHERE BA.LECCODE = BB.LECCODE AND BA.SUBJECT_TEACHER = BC.USER_ID AND BB.BRIDGE_LECCODE = BD.BRIDGE_LECCODE(+)) CA, TB_OFF_LEC_MST CB WHERE CA.RANK = 1 AND CA.LECCODE = CB.LECCODE AND CB.LEC_TYPE_CHOICE = 'D'
AND CB.CATEGORY_CD = #{SEARCHKIND}
AND CB.LEARNING_CD = #{SEARCHFORM}
AND CB.SUBJECT_OPEN_DATE >= '2012'
AND CB.SUBJECT_OPEN_DATE BETWEEN ${SEARCH_OPEN_DATE} AND ${SEARCH_END_DATE}
AND CB.SUBJECT_ISUSE = #{SEARCH_SUBJECT_ISUSE}
AND CB.LEC_PROCESS = #{SEARCH_LEC_PROCESS}
AND (CB.SUBJECT_SJT_CD like '%${SEARCHTEXT}%' OR CA.SUBJECT_NM like '%${SEARCHTEXT}%')
AND INSTR(CB.SUBJECT_TITLE, #{SEARCHTEXT}) > 0
AND CA.SUBJECT_TEACHER_NM like '%${SEARCHTEXT}%'
AND (CA.BRIDGE_LECCODE like '%${SEARCHTEXT}%' OR CB.LECCODE like '%${SEARCHTEXT}%')
AND (CB.SUBJECT_SJT_CD LIKE '%${SEARCHTEXT}%' OR CA.SUBJECT_NM LIKE '%${SEARCHTEXT}%' OR INSTR(CB.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 OR CA.SUBJECT_TEACHER_NM LIKE '%${SEARCHTEXT}%' OR CA.BRIDGE_LECCODE LIKE '%${SEARCHTEXT}%' OR CB.LECCODE LIKE '%${SEARCHTEXT}%')
ORDER BY CB.SEQ DESC ) DA WHERE rownum <= #{endNo} ) LTBL WHERE rnum > #{startNo}

SELECT COUNT(*) FROM (SELECT BA.LECCODE, BA.SUBJECT_ISUSE, BA.LEC_PROCESS, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = BA.SUBJECT_SJT_CD) AS SUBJECT_NM, BA.CATEGORY_CD, BA.LEARNING_CD, BA.SUBJECT_TITLE, BA.SUBJECT_TEACHER, BC.USER_NM SUBJECT_TEACHER_NM, BB.BRIDGE_LECCODE, RANK () OVER(PARTITION BY BB.BRIDGE_LECCODE ORDER BY BA.CATEGORY_CD ASC) RANK, NVL(BD.CNT, 0) CNT FROM TB_OFF_LEC_MST BA, TB_OFF_LEC_BRIDGE BB, TB_MA_MEMBER BC, (SELECT BRIDGE_LECCODE, COUNT(BRIDGE_LECCODE) CNT FROM (SELECT AC.BRIDGE_LECCODE FROM TB_OFF_MYLECTURE AA, TB_OFF_ORDER_MGNT_NO AB, TB_OFF_LEC_BRIDGE AC, (SELECT A.LECCODE, B.BRIDGE_LECCODE FROM TB_OFF_LEC_MST A, TB_OFF_LEC_BRIDGE B WHERE A.LECCODE = B.LECCODE) AD WHERE AA.ORDERNO = AB.ORDERNO AND AA.PACKAGE_NO = AB.MGNTNO AND AA.LECTURE_NO = AC.LECCODE AND AB.STATUSCODE IN ('DLV105', 'DLV230') AND AC.LECCODE = AD.LECCODE AND AC.BRIDGE_LECCODE = AD.BRIDGE_LECCODE GROUP BY AB.ORDERNO, AB.MGNTNO, AC.BRIDGE_LECCODE HAVING COUNT(AB.MGNTNO) = 1) GROUP BY BRIDGE_LECCODE) BD WHERE BA.LECCODE = BB.LECCODE AND BA.SUBJECT_TEACHER = BC.USER_ID AND BB.BRIDGE_LECCODE = BD.BRIDGE_LECCODE(+)) CA, TB_OFF_LEC_MST CB WHERE CA.RANK = 1 AND CA.LECCODE = CB.LECCODE AND CB.LEC_TYPE_CHOICE = 'D'
AND CB.CATEGORY_CD = #{SEARCHKIND}
AND CB.LEARNING_CD = #{SEARCHFORM}
AND CB.SUBJECT_OPEN_DATE >= '2012'
AND CB.SUBJECT_OPEN_DATE BETWEEN ${SEARCH_OPEN_DATE} AND ${SEARCH_END_DATE}
AND CB.SUBJECT_ISUSE = #{SEARCH_SUBJECT_ISUSE}
AND CB.LEC_PROCESS = #{SEARCH_LEC_PROCESS}
AND (CB.SUBJECT_SJT_CD like '%${SEARCHTEXT}%' OR CA.SUBJECT_NM like '%${SEARCHTEXT}%')
AND INSTR(CB.SUBJECT_TITLE, #{SEARCHTEXT}) > 0
AND CA.SUBJECT_TEACHER_NM like '%${SEARCHTEXT}%'
AND (CA.BRIDGE_LECCODE like '%${SEARCHTEXT}%' OR CB.LECCODE like '%${SEARCHTEXT}%')
AND (CB.SUBJECT_SJT_CD LIKE '%${SEARCHTEXT}%' OR CA.SUBJECT_NM LIKE '%${SEARCHTEXT}%' OR INSTR(CB.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 OR CA.SUBJECT_TEACHER_NM LIKE '%${SEARCHTEXT}%' OR CA.BRIDGE_LECCODE LIKE '%${SEARCHTEXT}%' OR CB.LECCODE LIKE '%${SEARCHTEXT}%')

		SELECT LTBL.*
			,(SELECT NAME FROM TB_CATEGORY_INFO WHERE ISUSE = 'Y' AND CODE = LTBL.CATEGORY_CD) CATEGORY_NM
			,(SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE ISUSE = 'Y' AND CODE = LTBL.LEARNING_CD) LEARNING_NM
		FROM 	(
					SELECT TBL.*, ROWNUM rnum
				  	FROM (
						SELECT
						  SEQ, RSC_ID, SUBJECT_SJT_CD, CATEGORY_CD, LEARNING_CD, BOOK_NM
						FROM TB_CA_BOOK WHERE USE_YN = 'Y'

					 		AND CATEGORY_CD = #{SEARCHKIND}


					 		AND LEARNING_CD = #{SEARCHFORM}


							AND BOOK_NM like '%'||#{SEARCHTEXT}||'%'

						ORDER BY REG_DT DESC
				) TBL
               	WHERE rownum <= #{endNo}
			) LTBL
		WHERE rnum > #{startNo}

	SELECT
	  COUNT(RSC_ID)
	FROM TB_CA_BOOK WHERE USE_YN = 'Y'

 		AND CATEGORY_CD = #{SEARCHKIND}


 		AND LEARNING_CD = #{SEARCHFORM}


		AND BOOK_NM like '%'||#{SEARCHTEXT}||'%'

mapper.insert:
INSERT INTO TB_OFF_LEC_MST(
	SEQ, LECCODE, CATEGORY_CD, LEARNING_CD, SUBJECT_TYPE
											, SUBJECT_GUBUN, SUBJECT_MEMBER_CNT , SUBJECT_TEACHER, SUBJECT_TEACHER_PAYMENT, SUBJECT_SJT_CD
											, SUBJECT_TITLE, SUBJECT_DESC , SUBJECT_KEYWORD, SUBJECT_OPEN_DATE, LEC_SCHEDULE
											, WEEK1, WEEK2, WEEK3, WEEK4, WEEK5, WEEK6, WEEK7
											, SUBJECT_DISCOUNT , SUBJECT_PRICE, SUBJECT_REAL_PRICE , SUBJECT_SUMNAIL, SUBJECT_ISUSE
											, LEC_TYPE_CHOICE, LEC_GUBUN, REC_GUBUN, REG_DT, REG_ID, UPD_DT, UPD_ID, PLAN
											, LEC_COUNT, LEC_PROCESS
)VALUES(
	(SELECT NVL(MAX(SEQ),0)+1 FROM TB_OFF_LEC_MST), #{LECCODE}, #{CATEGORY_CD}, #{LEARNING_CD}, #{SUBJECT_TYPE}
			, #{SUBJECT_GUBUN}, #{SUBJECT_MEMBER_CNT} , #{SUBJECT_TEACHER}, #{SUBJECT_TEACHER_PAYMENT}, #{SUBJECT_SJT_CD}
			, #{SUBJECT_TITLE}, #{SUBJECT_DESC}, #{SUBJECT_KEYWORD}, #{SUBJECT_OPEN_DATE}, #{LEC_SCHEDULE}
			, #{WEEK1}, #{WEEK2}, #{WEEK3}, #{WEEK4}, #{WEEK5}, #{WEEK6}, #{WEEK7}
			, #{SUBJECT_DISCOUNT}, #{SUBJECT_PRICE}, #{SUBJECT_REAL_PRICE}, #{SUBJECT_SUMNAIL}, #{SUBJECT_ISUSE}
			, #{LEC_TYPE_CHOICE}, #{LEC_GUBUN}, #{REC_GUBUN}, SYSDATE, #{REG_ID}, SYSDATE, #{UPD_ID}, #{PLAN}
			, #{LEC_COUNT}, #{LEC_PROCESS}
)

INSERT INTO TB_OFF_PLUS_CA_BOOK(IDX, LECCODE, RSC_ID, FLAG

, SUBJECT_BOOK_MEMO

) VALUES (
