mapper.update:
UPDATE TB_OFF_LEC_MST
	SET
		 LEARNING_CD = #{LEARNING_CD}

			,CATEGORY_CD = #{CATEGORY_CD}

		, SUBJECT_TYPE = #{SUBJECT_TYPE}
		, SUBJECT_GUBUN = #{SUBJECT_GUBUN}
		, SUBJECT_MEMBER_CNT = #{SUBJECT_MEMBER_CNT}
		, SUBJECT_TEACHER = #{SUBJECT_TEACHER}
		, SUBJECT_TEACHER_PAYMENT = #{SUBJECT_TEACHER_PAYMENT}
		, SUBJECT_SJT_CD = #{SUBJECT_SJT_CD}
		, SUBJECT_TITLE = #{SUBJECT_TITLE}
		, SUBJECT_DESC = #{SUBJECT_DESC}
		, SUBJECT_KEYWORD = #{SUBJECT_KEYWORD}
		, SUBJECT_OPEN_DATE = #{SUBJECT_OPEN_DATE}
		, LEC_SCHEDULE = #{LEC_SCHEDULE}
		, WEEK1 = #{WEEK1}
		, WEEK2 = #{WEEK2}
		, WEEK3 = #{WEEK3}
		, WEEK4 = #{WEEK4}
		, WEEK5 = #{WEEK5}
		, WEEK6 = #{WEEK6}
		, WEEK7 = #{WEEK7}
		, SUBJECT_DISCOUNT = #{SUBJECT_DISCOUNT}
		, SUBJECT_PRICE = #{SUBJECT_PRICE}
		, SUBJECT_REAL_PRICE = #{SUBJECT_REAL_PRICE}
		, SUBJECT_SUMNAIL = ''
		, SUBJECT_SUMNAIL = #{SUBJECT_SUMNAIL}
		, SUBJECT_ISUSE = #{SUBJECT_ISUSE}
		, LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
		, LEC_GUBUN = #{LEC_GUBUN}
		, REC_GUBUN = #{REC_GUBUN}
		, UPD_DT = SYSDATE
		, UPD_ID = #{UPD_ID}
		, PLAN = #{PLAN}

		, LEC_COUNT = #{LEC_COUNT}

		, LEC_PROCESS = DECODE(#{SUBJECT_ISUSE}, 'N', 'N', NVL(#{LEC_PROCESS}, #{SUBJECT_ISUSE}))
	WHERE LECCODE IN (SELECT LECCODE FROM TB_OFF_LEC_BRIDGE WHERE BRIDGE_LECCODE = #{BRIDGE_LEC} )
    WHERE LECCODE = #{LECCODE}

