WHERE LECCODE IN (SELECT LECCODE FROM TB_OFF_LEC_BRIDGE WHERE BRIDGE_LECCODE = #{BRIDGE_LEC} )
WHERE LECCODE = #{LECCODE}

UPDATE TB_OFF_LEC_MST SET
LEC_PROCESS = #{LEC_PROCESS}
SUBJECT_ISUSE = #{SUBJECT_ISUSE}
WHERE LECCODE IN ( SELECT TBL.LECCODE FROM ( SELECT SEQ, LECCODE FROM TB_OFF_LEC_JONG GROUP BY SEQ, LECCODE HAVING LECCODE LIKE 'N%' AND SEQ = #{JONGSEQ_ARR}) T1 INNER JOIN TB_OFF_LEC_MST TBL ON T1.LECCODE = TBL.LECCODE WHERE LEC_TYPE_CHOICE = 'N'
AND TBL.CATEGORY_CD = #{SEARCHKIND}
)
WHERE LECCODE IN ( SELECT A.LECCODE FROM TB_OFF_LEC_BRIDGE A LEFT OUTER JOIN TB_OFF_LEC_MST B ON A.LECCODE = B.LECCODE WHERE 1=1 AND A.BRIDGE_LECCODE = #{BRIDGE_LECCODES}
AND B.CATEGORY_CD = #{SEARCHKIND}
)

mapper.select:
SELECT TBL.* FROM ( SELECT T1.BRIDGE_LECCODE, T2.* , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = T2.CATEGORY_CD) AS CATEGORY_NM , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = T2.LEARNING_CD) AS LEARNING_NM , (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = T2.SUBJECT_SJT_CD) AS SUBJECT_NM , (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = T2.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM FROM TB_OFF_LEC_BRIDGE T1 RIGHT JOIN TB_OFF_LEC_MST T2 ON T1.LECCODE = T2.LECCODE ) TBL WHERE 1 = 1 AND LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
SELECT TBL.* , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = TBL.CATEGORY_CD) AS CATEGORY_NM , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = TBL.LEARNING_CD) AS LEARNING_NM FROM TB_OFF_LEC_MST TBL WHERE LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
AND 1 = 2
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.CATEGORY_CD = #{SEARCHKIND}
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%')
AND TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
AND (TBL.BRIDGE_LECCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.LECCODE like '%'||#{SEARCHTEXT}||'%')
AND (TBL.SUBJECT_SJT_CD like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%' OR TBL.BRIDGE_LECCODE like '%'||#{SEARCHTEXT}||'%' OR TBL.LECCODE like '%'||#{SEARCHTEXT}||'%')
ORDER BY TBL.SEQ DESC

SELECT LTBL.* FROM ( SELECT A.*, ROWNUM rnum FROM ( SELECT TBL.* , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = TBL.CATEGORY_CD) AS CATEGORY_NM , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = TBL.LEARNING_CD) AS LEARNING_NM , T1.JONGSEQ, NVL(A1.CNT,0) USR_CNT FROM (SELECT JONGSEQ, LECCODE FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY A.SEQ ORDER BY B.CATEGORY_CD) ROW_NUM, A.LECCODE, A.SEQ JONGSEQ FROM TB_OFF_LEC_JONG A, TB_OFF_LEC_MST B WHERE A.LECCODE = B.LECCODE
AND B.CATEGORY_CD = #{SEARCHKIND}
) WHERE ROW_NUM = 1) T1, TB_OFF_LEC_MST TBL, (SELECT MGNTNO, COUNT(MGNTNO) CNT FROM ( SELECT ORDERNO, MGNTNO FROM TB_OFF_ORDER_MGNT_NO WHERE STATUSCODE IN ('DLV105', 'DLV230') GROUP BY ORDERNO, MGNTNO HAVING COUNT(MGNTNO) = 1) AA GROUP BY MGNTNO) A1 WHERE T1.LECCODE = TBL.LECCODE AND T1.LECCODE = A1.MGNTNO(+) AND TBL.LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
AND LEARNING_CD = #{SEARCHFORM}
AND SUBJECT_OPEN_DATE >= '2012'
AND SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.LECCODE like '%'||#{SEARCHTEXT}||'%'
AND ( SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.LECCODE like '%'||#{SEARCHTEXT}||'%')
ORDER BY TBL.SEQ DESC ) A WHERE rownum <= #{endNo} ) LTBL WHERE rnum > #{startNo}

SELECT COUNT(TBL.LECCODE) FROM (SELECT JONGSEQ, LECCODE FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY A.SEQ ORDER BY B.CATEGORY_CD) ROW_NUM ,A.LECCODE, A.SEQ JONGSEQ FROM TB_OFF_LEC_JONG A LEFT OUTER JOIN TB_OFF_LEC_MST B ON A.LECCODE = B.LECCODE WHERE A.LECCODE LIKE CONCAT(#{LEC_TYPE_CHOICE}, '%')
AND B.CATEGORY_CD = #{SEARCHKIND}
) WHERE ROW_NUM = 1) T1 INNER JOIN TB_OFF_LEC_MST TBL ON T1.LECCODE = TBL.LECCODE WHERE LEC_TYPE_CHOICE = #{LEC_TYPE_CHOICE}
AND LEARNING_CD = #{SEARCHFORM}
AND SUBJECT_OPEN_DATE >= '2012'
AND SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.LECCODE like '%'||#{SEARCHTEXT}||'%'
AND ( SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.LECCODE like '%'||#{SEARCHTEXT}||'%')

		SELECT * FROM 	(
			SELECT A.*, ROWNUM rnum
		  	FROM (
                SELECT TBL.* FROM (
                    SELECT T1.*
                        , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = T1.CATEGORY_CD) AS CATEGORY_NM
                        , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = T1.LEARNING_CD) AS LEARNING_NM
                        , (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = T1.SUBJECT_SJT_CD) AS SUBJECT_NM
                        , (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = T1.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM
                        FROM
                        TB_OFF_LEC_MST T1 INNER JOIN TB_OFF_LEC_BRIDGE T2 ON T1.LECCODE = T2.LECCODE
                    WHERE T1.LEC_TYPE_CHOICE = 'D'
                ) TBL WHERE 1 = 1

		 			AND TBL.CATEGORY_CD = #{SEARCHKIND}


		 			AND TBL.LEARNING_CD = #{SEARCHFORM}




							AND TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'


							AND TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'



						AND (
							TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
							OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
						)


				ORDER BY TBL.SEQ DESC
			) A
            WHERE rownum <= #{endNo}
		)
		WHERE rnum > #{startNo}

SELECT COUNT(TBL.LECCODE) FROM ( SELECT T1.* , (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = T1.CATEGORY_CD) AS CATEGORY_NM , (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = T1.LEARNING_CD) AS LEARNING_NM , (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = T1.SUBJECT_SJT_CD) AS SUBJECT_NM , (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = T1.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM FROM TB_OFF_LEC_MST T1 INNER JOIN TB_OFF_LEC_BRIDGE T2 ON T1.LECCODE = T2.LECCODE WHERE T1.LEC_TYPE_CHOICE = 'D' ) TBL WHERE 1 = 1
AND TBL.CATEGORY_CD = #{SEARCHKIND}
AND TBL.LEARNING_CD = #{SEARCHFORM}
AND TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%'
AND TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%'
AND ( TBL.SUBJECT_TITLE like '%'||#{SEARCHTEXT}||'%' OR TBL.SUBJECT_TEACHER_NM like '%'||#{SEARCHTEXT}||'%' )

SELECT C.USERID AS USERID ,(SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = C.USERID) AS USER_NM ,(SELECT PHONE_NO FROM TB_MA_MEMBER WHERE USER_ID = C.USERID) AS PHONE_NO ,(SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = A.SUBJECT_TEACHER) AS SUBJECT_USER_NM ,A.SUBJECT_TITLE AS SUBJECT_TITLE ,C.START_DATE AS START_DATE ,C.END_DATE AS END_DATE FROM TB_OFF_LEC_MST A ,( SELECT A.ORDERNO ,A.MGNTNO ,A.WMV_PMP FROM TB_OFF_ORDER_MGNT_NO A ,(SELECT ORDERNO, MGNTNO FROM TB_OFF_ORDER_MGNT_NO GROUP BY ORDERNO, MGNTNO HAVING COUNT(*) = 1 ) B WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.MGNTNO AND A.STATUSCODE = 'DLV105' AND SUBSTR(A.MGNTNO,1,1) <> 'L' AND A.WMV_PMP = #{SEARCHPAYTYPE}
AND A.PRICE > 0
AND A.PRICE = 0
) B ,TB_MYLECTURE C WHERE A.LECCODE = C.LECTURE_NO AND B.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND SUBSTR(C.PACKAGE_NO,1,1) NOT IN ('D','L') AND B.WMV_PMP = #{SEARCHPAYTYPE} AND C.PACKAGE_NO = #{LECCODE} ORDER BY C.LECTURE_NO

mapper.delete:
DELETE FROM TB_OFF_LECTURE_DATE
WHERE LECCODE IN (SELECT LECCODE FROM TB_OFF_LEC_BRIDGE WHERE BRIDGE_LECCODE = #{BRIDGE_LEC} )
WHERE LECCODE = #{LECCODE}
