mapper.insert:
INSERT INTO TB_CATEGORY_INFO (
    ID
    , CODE
    , NAME
    , ISUSE
    , USE_ON 
    , USE_OFF 
    , ORDR 
    , P_CODE 
    , REG_DT
    , REG_ID
) VALUES (
    SEQ_TB_CATEGORY_INFO_ID.NEXTVAL
    , #{CODE}
    , #{NAME}
    , #{ISUSE}
    , #{USE_ON} 
    , #{USE_OFF} 
    , #{ORDR} 
    , #{P_CODE} 
    , SYSDATE
    , #{REG_ID}
)

mapper.update:
UPDATE TB_CATEGORY_INFO
SET
  NAME = #{NAME}
  , ISUSE = #{ISUSE}
  , USE_ON = #{USE_ON} 
  , USE_OFF = #{USE_OFF} 
  , ORDR = #{ORDR} 
  , UPD_ID = #{UPD_ID}
  , UPD_DT = SYSDATE
WHERE CODE = #{CODE}
