select.if:
SELECT
    USER_ID, CATEGORY_CODE, SEQ FROM
TB_MA_MEMBER_CATEGORY WHERE USER_ID = #{USER_ID}

SELECT A.SUBJECT_CD, A.SUBJECT_NM, A.SUBJECT_SHORT_NM, A.ISUSE , DECODE(A.ISUSE, 'Y', '활성', 'N', '비활성') ISUSENM , A.REG_DT, A.REG_ID, A.UPD_DT, A.UPD_ID FROM TB_SUBJECT_INFO A, ( SELECT SUBJECT_CD FROM TB_CATEGORY_SUBJECT_ORDER
WHERE ONOFF_DIV = #{ONOFF_DIV}
GROUP BY SUBJECT_CD) B WHERE A.SUBJECT_CD = B.SUBJECT_CD AND A.ISUSE = 'Y' ORDER BY A.REG_DT ASC, A.SUBJECT_NM ASC

SELECT A.SUBJECT_CD, A.SUBJECT_NM, A.SUBJECT_SHORT_NM FROM TB_SUBJECT_INFO A, ( SELECT SUBJECT_CD FROM TB_CATEGORY_SUBJECT_ORDER
WHERE ONOFF_DIV = #{ONOFF_DIV}
GROUP BY SUBJECT_CD) B WHERE A.SUBJECT_CD = B.SUBJECT_CD AND A.ISUSE = 'Y' AND A.SUBJECT_CD NOT IN (SELECT SUBJECT_CD FROM TB_MA_MEMBER_SUBJECT WHERE USER_ID = #{USER_ID}
AND USE_OFF = 'Y' AND USE_ON = 'Y'
) ORDER BY A.SUBJECT_NM ASC

SELECT A.SUBJECT_CD, A.SUBJECT_NM, A.SUBJECT_SHORT_NM FROM TB_SUBJECT_INFO A, ( SELECT SUBJECT_CD FROM TB_CATEGORY_SUBJECT_ORDER
WHERE ONOFF_DIV = #{ONOFF_DIV}
GROUP BY SUBJECT_CD) B WHERE A.SUBJECT_CD = B.SUBJECT_CD AND A.ISUSE = 'Y' AND A.SUBJECT_CD IN (SELECT SUBJECT_CD FROM TB_MA_MEMBER_SUBJECT WHERE USER_ID = #{USER_ID}
AND USE_OFF = 'Y' AND USE_ON = 'Y'
) ORDER BY A.SUBJECT_NM ASC

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM rnum FROM (
SELECT * FROM ( SELECT AA.USER_ID, AA.USER_NM, AA.ISUSE, AA.EMAIL, AA.PHONE_NO, AA.USER_POSITION , DECODE(AA.ISUSE, 'Y', '공개', 'N', '비공개') ISUSENM , AA.REG_DT, AA.BIRTH_DAY, AA.ON_OPENYN, AA.OFF_OPENYN , DECODE(NVL(ON_OPENYN,'Y'), 'Y', '활성', 'N', '비활성') ON_OPENYNNM , DECODE(NVL(OFF_OPENYN,'Y'), 'Y', '활성', 'N', '비활성') OFF_OPENYNNM , (SELECT MIN(SUBJECT_CD) FROM TB_MA_MEMBER_SUBJECT WHERE USER_ID = AA.USER_ID) AS SUBJECT_CD , (SELECT LISTAGG(DD.SUBJECT_NM,'/') WITHIN GROUP(ORDER BY DD.SUBJECT_NM) FROM TB_MA_MEMBER_SUBJECT CC LEFT JOIN TB_SUBJECT_INFO DD ON CC.SUBJECT_CD = DD.SUBJECT_CD WHERE USER_ID = AA.USER_ID ) AS SUBJECT_NM , (SELECT MIN(CATEGORY_CODE) FROM TB_MA_MEMBER_CATEGORY WHERE USER_ID = AA.USER_ID) AS CATEGORY_CD , (SELECT LISTAGG('['||DD.NAME||'] ','') WITHIN GROUP(ORDER BY CC.CATEGORY_CODE ASC) FROM TB_MA_MEMBER_CATEGORY CC LEFT JOIN TB_CATEGORY_INFO DD ON CC.CATEGORY_CODE = DD.CODE WHERE USER_ID = AA.USER_ID ) AS CATEGORY_NM , MAX(BB.SEQ) SEQ , MAX(BB.OFF_SEQ) AS OFF_SEQ FROM TB_MA_MEMBER AA LEFT JOIN TB_MA_MEMBER_CATEGORY BB ON AA.USER_ID = BB.USER_ID WHERE AA.USER_ROLE = 'PRF' AND AA.ISUSE = 'Y'
AND BB.CATEGORY_CODE = #{SEARCHCATEGORY}
AND INSTR(AA.USER_ID, #{SEARCHTEXT}) > 0
AND INSTR(AA.USER_NM, #{SEARCHTEXT}) > 0
AND ( INSTR(AA.USER_ID, #{SEARCHTEXT}) > 0 OR INSTR(AA.USER_NM, #{SEARCHTEXT}) > 0 )
GROUP BY AA.USER_ID, AA.USER_NM, AA.ISUSE, AA.EMAIL, AA.PHONE_NO, AA.USER_POSITION, AA.REG_DT, AA.UPD_DT, AA.USER_ROLE, AA.BIRTH_DAY, AA.ON_OPENYN, AA.OFF_OPENYN )
WHERE SUBJECT_CD = #{SEARCHSBJ}
ORDER BY CATEGORY_CD, SUBJECT_CD , USER_NM
WHERE OFF_OPENYN = 'Y' ORDER BY CATEGORY_CD, OFF_SEQ, SUBJECT_CD , SEQ, DECODE(ISUSE,'Y',1,2), DECODE(OFF_OPENYN,'Y',1,2), DECODE(ON_OPENYN,'Y',1,2)
WHERE ON_OPENYN = 'Y' ORDER BY CATEGORY_CD, OFF_SEQ, SUBJECT_CD , SEQ, DECODE(ISUSE,'Y',1,2), DECODE(OFF_OPENYN,'Y',1,2), DECODE(ON_OPENYN,'Y',1,2)
ORDER BY CATEGORY_CD, SEQ, SUBJECT_CD , SEQ, DECODE(ISUSE,'Y',1,2), DECODE(ON_OPENYN,'Y',1,2), DECODE(OFF_OPENYN,'Y',1,2)
) A WHERE rownum <= #{endNo} ) WHERE rnum > #{startNo}

