SELECT * FROM (SELECT ROWNUM AS RNUM, H.* FROM (SELECT A.ORDERNO, A.ORDER_TYPE , DECODE(A.ORDER_TYPE, 'ON', '온라인신청', 'OFF', '학원방문', '데스크접수') ORDER_TYPE_NM , TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT, ISCONFIRM , A.USER_ID, A.USER_NM, A.PHONE_NO, A.TICKET_PRINT_DT, D.SUBJECT_TITLE , AA.CNT SUBJECT_CNT, B.PRICE, C.STATUSCODE , SUBSTR(C.MGNTNO,1,1) MGNTNO , DECODE(C.STATUSCODE, 'DLV105', '결제완료', 'DLV230', '환불완료', 'DLV250', '대기중', '') STATUSCODE_NM , FN_GET_OFF_PAY_NM(A.ORDERNO) PRICE_STS ,D.CATEGORY_CD, (SELECT E.NAME FROM TB_CATEGORY_INFO E WHERE E.CODE = D.CATEGORY_CD) CATEGORY_NM FROM (SELECT B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD') ISCONFIRM, COUNT(C.LECTURE_NO) CNT FROM TB_OFF_ORDERS A, TB_OFF_ORDER_MGNT_NO B, TB_OFF_MYLECTURE C WHERE A.ORDERNO = B.ORDERNO AND A.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND B.STATUSCODE IN ('DLV105', 'DLV250')
AND TO_CHAR(B.ISCONFIRM, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
GROUP BY B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD')) AA, (SELECT ORDERNO, MAX(STATUSCODE) STATUSCODE, MAX(MGNTNO) MGNTNO FROM TB_OFF_ORDER_MGNT_NO WHERE PTYPE NOT IN('S','T') GROUP BY ORDERNO) C, TB_OFF_ORDERS A, TB_OFF_APPROVALS B, TB_OFF_LEC_MST D WHERE C.ORDERNO = A.ORDERNO(+) AND C.ORDERNO = B.ORDERNO(+) AND C.ORDERNO = AA.ORDERNO(+) AND C.MGNTNO = D.LECCODE
AND A.ORDER_TYPE = #{ordertype}
AND C.STATUSCODE = #{orderstatuscode}
AND B.PAYCODE = #{orderpaycode}
AND REPLACE(ISCONFIRM, '-','') BETWEEN #{sdate} and #{edate}
AND D.CATEGORY_CD = #{s_cat_cd}
AND INSTR(${searchtype}, #{searchkey}) > 0
AND ( INSTR(A.ORDERNO, #{searchkey}) > 0 OR INSTR(A.USER_NM, #{searchkey}) > 0)
ORDER BY A.REG_DT DESC, A.ORDERNO DESC) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}

SELECT count(*) FROM (SELECT B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD') ISCONFIRM, COUNT(C.LECTURE_NO) CNT FROM TB_OFF_ORDERS A, TB_OFF_ORDER_MGNT_NO B, TB_OFF_MYLECTURE C WHERE A.ORDERNO = B.ORDERNO AND A.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND B.STATUSCODE IN ('DLV105', 'DLV250')
AND TO_CHAR(B.ISCONFIRM, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
GROUP BY B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD')) AA, (SELECT ORDERNO, MAX(STATUSCODE) STATUSCODE, MAX(MGNTNO) MGNTNO FROM TB_OFF_ORDER_MGNT_NO WHERE PTYPE NOT IN('S','T') GROUP BY ORDERNO) C, TB_OFF_ORDERS A, TB_OFF_APPROVALS B, TB_OFF_LEC_MST D WHERE C.ORDERNO = A.ORDERNO(+) AND C.ORDERNO = B.ORDERNO(+) AND C.ORDERNO = AA.ORDERNO(+) AND C.MGNTNO = D.LECCODE
AND A.ORDER_TYPE = #{ordertype}
AND C.STATUSCODE = #{orderstatuscode}
AND B.PAYCODE = #{orderpaycode}
AND REPLACE(ISCONFIRM, '-','') BETWEEN #{sdate} and #{edate}
AND D.CATEGORY_CD = #{s_cat_cd}
AND INSTR(${searchtype}, #{searchkey}) > 0
AND ( INSTR(A.ORDERNO, #{searchkey}) > 0 OR INSTR(A.USER_NM, #{searchkey}) > 0)

select aa.mgntNo, aa.name, aa.cnt, nvl(aa.price,0) as price, nvl(aa.statusCode,'DLV000') as statusCode, aa.statusCodeNm, aa.wmv_pmp, aa.user_package_flag, aa.confirmDate, aa.confirmDate2, aa.cancelDate, aa.isconfirm, aa.realprice, aa.isCancel, aa.confirmid from( select b.leccode as mgntNo, b.subject_title as name, a.cnt, a.price, a.statusCode, (SELECT NVL(CODE_NM,' ') FROM TB_BA_CONFIG_CD where SYS_CD = 'ORDER_STATUSCODE' and ISUSE = 'Y' and CODE_VAL = a.statusCode) statusCodeNm, a.wmv_pmp, a.user_package_flag, decode(a.confirmDate, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD')) confirmDate, decode(a.confirmDate, null, TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), '', TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD hh24:mi:ss')) confirmDate2, a.cancelDate, decode(a.isconfirm, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.isconfirm, 'YYYY-MM-DD')) isconfirm, 0 AS realprice, a.isCancel, a.confirmid from TB_OFF_ORDER_MGNT_NO a, TB_OFF_LEC_MST b where a.orderNo = #{orderno} and a.mgntNo = b.leccode
and a.statusCode = #{orderstatuscode}
union all
select b.rsc_id as mgntNo, b.book_nm as name, a.cnt, a.price, a.statusCode, (SELECT NVL(CODE_NM, ' ') FROM TB_BA_CONFIG_CD where SYS_CD = 'ORDER_STATUSCODE' and ISUSE = 'Y' and CODE_VAL = a.statusCode) statusCodeNm, a.wmv_pmp, a.user_package_flag, TO_CHAR(a.confirmDate, 'YYYY-MM-DD') confirmDate, decode(a.confirmDate, null, TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), '', TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD hh24:mi:ss')) confirmDate2, a.cancelDate, TO_CHAR(a.isconfirm, 'YYYY-MM-DD') isconfirm, b.price as realprice, a.isCancel, a.confirmid from TB_OFF_ORDER_MGNT_NO a, TB_CA_BOOK b where a.orderNo = #{orderno} and a.mgntNo = b.rsc_id
and a.statusCode = #{orderstatuscode}
)aa order by aa.mgntNo

SELECT * FROM ( SELECT ROWNUM AS RNUM, AA.* FROM ( SELECT A.LECCODE, A.CATEGORY_CD, A.LEARNING_CD, A.SUBJECT_SJT_CD, A.SUBJECT_TITLE, A.LEC_SCHEDULE, A.SUBJECT_REAL_PRICE, A.LEC_TYPE_CHOICE, DECODE(A.SUBJECT_TYPE, '1', '실강', '2', '실시간 영상') SUBJECT_TYPE_NM, B.ST_LEC_DATE, B.ED_LEC_DATE, C.NAME CATEGORY_NM, D.NAME LEARNING_NM, E.SUBJECT_NM, F.USER_NM TEACHER_NM, CASE WHEN B.ST_LEC_DATE > TO_CHAR(SYSDATE, 'YYYY-MM-DD') THEN '진행전' WHEN TO_CHAR(SYSDATE, 'YYYY-MM-DD') BETWEEN B.ST_LEC_DATE AND B.ED_LEC_DATE THEN '진행중' ELSE '종강' END LEC_STAT FROM TB_OFF_LEC_MST A, (SELECT LECCODE, MIN(LEC_DATE) ST_LEC_DATE, MAX(LEC_DATE) ED_LEC_DATE FROM TB_OFF_LECTURE_DATE GROUP BY LECCODE) B, TB_CATEGORY_INFO C, TB_LEARNING_FORM_INFO D, TB_SUBJECT_INFO E, TB_MA_MEMBER F WHERE A.LECCODE = B.LECCODE AND A.LEC_TYPE_CHOICE = 'D' AND A.CATEGORY_CD = C.CODE AND A.LEARNING_CD = D.CODE AND A.SUBJECT_SJT_CD = E.SUBJECT_CD AND A.SUBJECT_TEACHER = F.USER_ID ORDER BY A.SUBJECT_OPEN_DATE DESC) AA WHERE AA.LEC_TYPE_CHOICE = 'D'
